USE test;

-- 존재하는 테이블 삭제
DROP TABLE IF EXISTS Reviews;
DROP TABLE IF EXISTS Recipes_And_Ingredients;
DROP TABLE IF EXISTS Meal_logs_And_Ingredients;
DROP TABLE IF EXISTS Ingredients;
DROP TABLE IF EXISTS Meal_logs;
DROP TABLE IF EXISTS Recipes;
DROP TABLE IF EXISTS Members;

-- Members 테이블 생성
CREATE TABLE Members (
    member_email VARCHAR(255) PRIMARY KEY,
    password VARCHAR(255) NOT NULL,
    name VARCHAR(255) NOT NULL,
    sex INT NOT NULL,
    activity_level INT NOT NULL,
    weight INT NOT NULL,
    height INT NOT NULL
);

-- Meal_logs 테이블 생성
CREATE TABLE Meal_logs (
    meal_log_id INT PRIMARY KEY AUTO_INCREMENT,
    member_email VARCHAR(255) NOT NULL,
    log_date DATE NOT NULL,
    meal INT NOT NULL,
    picture VARCHAR(255),
    FOREIGN KEY (member_email) REFERENCES Members(member_email)
);

-- Ingredients 테이블 생성 (변경된 구조 반영)
CREATE TABLE Ingredients (
    ingredient_id INT PRIMARY KEY AUTO_INCREMENT,
    name VARCHAR(255) NOT NULL,
    exchange_group INT NOT NULL,
    exchange_amount FLOAT NOT NULL,
    carbohydrates FLOAT NOT NULL,
    proteins FLOAT NOT NULL,
    fats FLOAT NOT NULL,
    calorie FLOAT NOT NULL
);

-- Recipes 테이블 생성
CREATE TABLE Recipes (
    recipe_id INT PRIMARY KEY AUTO_INCREMENT,
    weight FLOAT NOT NULL,
    name VARCHAR(255) NOT NULL
);

-- Recipes_And_Ingredients 테이블 생성
CREATE TABLE Recipes_And_Ingredients (
    id INT PRIMARY KEY AUTO_INCREMENT,
    recipe_id INT NOT NULL,
    ingredient_id INT NOT NULL,
    FOREIGN KEY (recipe_id) REFERENCES Recipes(recipe_id),
    FOREIGN KEY (ingredient_id) REFERENCES Ingredients(ingredient_id)
);

-- Meal_logs_And_Ingredients 테이블 생성
CREATE TABLE Meal_logs_And_Ingredients (
    id INT PRIMARY KEY AUTO_INCREMENT,
    meal_log_id INT NOT NULL,
    ingredient_id INT NOT NULL,
    FOREIGN KEY (meal_log_id) REFERENCES Meal_logs(meal_log_id),
    FOREIGN KEY (ingredient_id) REFERENCES Ingredients(ingredient_id)
);

-- Reviews 테이블 생성
CREATE TABLE Reviews (
    review_id INT PRIMARY KEY AUTO_INCREMENT,
    recipe_id INT,
    member_email VARCHAR(255),
    rating INT NOT NULL,
    comment TEXT,
    modified_date DATE,
    FOREIGN KEY (recipe_id) REFERENCES Recipes(recipe_id),
    FOREIGN KEY (member_email) REFERENCES Members(member_email)
);




